    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
float valor_custo = Integer.parseInt(caixa1.getText());
float valor_venda = Integer.parseInt(caixa2.getText());
float valor_resultado = valor_venda - valor_custo;

if(valor_custo < valor_venda){
caixaFim.setText("Houve lucro de "+valor_resultado);
}else if(valor_venda< valor_custo){
    caixaFim.setText("Houve prejuizo de "+valor_resultado);
}else {
      caixaFim.setText("NÃ£o houve lucro "+valor_resultado);
}
    }//GEN-LAST:event_jButton1ActionPerformed

